$Users = Get-ADUser -Filter * -SearchBase "OU=Test,DC=mcauliffe-systems,DC=com" -Properties Manager| Select SAMAccountName,Manager $Manager = @{n="Manager";e={(Get-ADUser -Identity $_.Manager -Properties sAMAccountName).sAMAccountName}},                                                  @{n="Manager Enabled";e={(Get-ADUser -Identity $_.Manager -Properties Enabled).Enabled}}$Manager | ?{$_.Manager -eq $null}                        #Export-Csv -Path "\\DC1\C$\Users\Administrator\Desktop\users.csv" -NoTypeInformation